import Answer from "./Answers";

let q1 ={ 
      question: "When ReactJS released?",
      answers:["JUN 2015", "March 2014", "March 2013"], 
      right_answer:"March 2013"
    }





export default class Tests extends Component {

    render() {
        return (
          <div>
            <h1 className="MainTestHeader">EASY</h1>
            <div className="Q"> 
                <h2 className="T"> { q1.question}</h2> 
                <div className="AnswersAll">
                <Answer right_answer={ q1.right_answer} answer={ q1.answers} />
                </div>
            </div>

            <div className="Q"> 
                <h2 className="T"> { q1.question}</h2> 
                <div className="AnswersAll">
                <Answer right_answer={ q1.right_answer} answer={ q1.answers} />
                </div>
            </div>

            <div className="Q"> 
                <h2 className="T"> { q1.question}</h2> 
                <div className="AnswersAll">
                <Answer right_answer={ q1.right_answer} answer={ q1.answers} />
                </div>
            </div>


          </div>

        )
    }
}
