import React, { Component, Fragment } from "react";
import './Tests.css';


export default class Tests extends Component {
    //  create state
    state = {
        Answers: this.props.anwer,
        Clickcheck:true,
        rightAnaswer: this.props.rightAnaswer
    };

    // Event on button
    onAnswer = () => {

        this.setState({ 
        Clickcheck:false
        });

    };

  render() {



    return (
      <Fragment>
        { this.state.Clickcheck ? this.state.Answers.map(ans => {
          return <button className="answ" onClick={this.onAnswer}> {ans}</button>;
        }) : <button className="answ"> {this.state.rightAnaswer}</button>  } 

      </Fragment>
    );
  }

}
